mapper.select:
select BOD_NO, TITLE, CONTS, CRE_DT, VW_CNT, WRITER from BOARD where TYPE=1
order by BOD_NO desc
limit #{startIndex}, #{length}

select distinct D.BOD_NO, D.TITLE, D.CONTS, D.CRE_DT, D.VW_CNT, D.WRITER from re A left join re_crct B on A.build_no = B.build_no left join membs C on B.T_EMAIL = C.EMAIL left join board D on C.EMAIL = D.EMAIL where A.L_EMAIL=#{email} and D.TYPE=1 and D.RSVD=0
order by lastUpdated desc
limit #{startIndex}, #{length}

select distinct D.BOD_NO, D.TITLE, D.CONTS, D.CRE_DT, D.VW_CNT, D.WRITER from re A left join re_crct B on A.build_no = B.build_no left join membs C on B.T_EMAIL = C.EMAIL left join board D on C.EMAIL = D.EMAIL where A.L_EMAIL=#{email} and D.TYPE=1 and D.RSVD=1
order by lastUpdated desc
limit #{startIndex}, #{length}

select bod_no,title,cre_dt,vw_cnt,conts,writer
from BOARD
where bod_no = #{value}

mapper.update:
update board set 
  TITLE=#{title}, 
  CONTS=#{contents},
  RSVD=#{rsvd} 
where BOD_NO=#{no}
