mapper.select:
select BOD_NO, TITLE, CONTS, CRE_DT, VW_CNT, WRITER from BOARD where TYPE=1
order by BOD_NO desc
limit #{startIndex}, #{length}

select BOD_NO, TITLE, CONTS, CRE_DT, VW_CNT, WRITER from BOARD where TYPE=1 and RSVD=0
order by lastUpdated desc
limit #{startIndex}, #{length}

select BOD_NO, TITLE, CONTS, CRE_DT, VW_CNT, WRITER from BOARD where TYPE=1 and RSVD=1
order by lastUpdated desc
limit #{startIndex}, #{length}

select bod_no,title,cre_dt,vw_cnt,conts
from BOARD
where bod_no = #{value}

mapper.update:
update board set 
  TITLE=#{title}, 
  CONTS=#{contents},
  RSVD=#{rsvd} 
where BOD_NO=#{no}
